open(FILE, $ARGV[0])or die"no se abrio el probado\n";@archi=<FILE>;close(FILE);chomp(@archi);open(FILE, $ARGV[1])or die"no se abrio el target\n";@buenos=<FILE>;close(FILE);chomp(@buenos);@buenotes=();for($i=0; $i<scalar(@archi); $i++){	$flag=&busqueda($archi[$i], \@buenos);	if($flag==1){		push(@buenotes, $archi[$i]);	}}for($i=0; $i<scalar(@buenotes); $i++){	print"$buenotes[$i]\n";}sub busqueda{my ($buscado, $donde)=@_;	$flag=0;	foreach $este(@$donde){		$buscado=~/(Y\w\w)(\d\d\d)/;		$pre=$1;		$este=~/(Y\w\w)(\d\d\d)/;		$pre2=$1;		if($pre eq $pre2){			print"$buscado no es $este\n";		}		if($buscado eq $este){			$flag=1;			last;		}	}	return $flag;}