opendir(OD,".");@datos=readdir(OD);close(OD);@orfs=();for($i=0;$i<scalar(@datos);$i++){	if($datos[$i] =~ /csv1/){		next;	}	elsif($datos[$i] =~ /csv/){		open(NIU, $datos[$i])or die"valio queso en $datos[$i]\n";		@matriz=<NIU>;		close(NIU);	chomp(@matriz);		@orfex=split(";",$matriz[0]);		for($j=1; $j<scalar(@matriz); $j++){			if($matriz[$j] =~ /Gln3p/){				@gln3=split(";",$matriz[$j]);			}			if($matriz[$j] =~ /Gcn4p/){				@gcn4=split(";",$matriz[$j]);			}		}		$datos[$i] =~ /(e\dcy\d)/;		open(NIU,$datos[$i+1])or die"valio queso en $datos[$i+1]\n";		@ids=<NIU>;		chomp(@ids);		close(NIU);		for($k=0;$k<scalar(@orfex);$k++){			$esta=0;			for($j=0;$j<scalar(@orfs);$j++){				if($orfex[$k] eq $orfs[$j]){					$esta=1;					last;				}			}			if($esta==0){				push(@orfs,$orfex[$k]);				$tabla{$orfex[$k]}{'gln3'}=$gln3[$k+1];				$tabla{$orfex[$k]}{'gcn4'}=$gcn4[$k+1];				$tabla{$orfex[$k]}{'id'}=$ids[$k];				$tabla{$orfex[$k]}{'cont'}=1;				$tabla2{$orfex[$k]}[0]=$1;			}			else{				$tabla2{$orfex[$k]}[$tabla{$orfex[$k]}{'cont'}]=$1;				$tabla{$orfex[$k]}{'cont'}+=1;			}		}	}}for($i=0;$i<scalar(@orfs);$i++){	print"$orfs[$i]\t$tabla{$orfs[$i]}{'id'}\t$tabla{$orfs[$i]}{'gcn4'}\t$tabla{$orfs[$i]}{'gln3'}";	for($j=0;$j<$tabla{$orfs[$i]}{'cont'};$j++){		print"\t$tabla2{$orfs[$i]}[$j]";	}	print"\n";}