open(PRIM,$ARGV[0])or die"No se abrio el  primer archivo\n";@prim=<PRIM>;chomp(@prim);close(PRIM);foreach $va(@ARGV){	open(VAR, $va) or die "Error al abrir $va";	@nuevo=<VAR>;	close(VAR);	@tmp=();	foreach $gen1(@nuevo){		chomp($gen1);		$bandera=&busqueda($gen1, \@prim);		if($bandera==0){			push(@prim, $gen1);		}	}}foreach $r(@prim){	print"$r\n";}sub busqueda{my ($buscado, $donde)=@_;	$flag=0;	foreach $este(@$donde){		if($buscado eq $este){			$flag=1;			last;		}	}	return $flag;}